export enum LOG_LEVEL {
    OFF,
    ERROR,
    WARN,
    INFO,
    VERBOSE,
    DEBUG,
}

export namespace logger {
    function setLevel(level: LOG_LEVEL): void;
    function isLevel(level: LOG_LEVEL):boolean;
    const spinner: import("ora").Ora;
    function pauseSpinner(fn: Function, ...arg: any):void;
    function error(...args: any):void;
    function warning(...args: any):void;
    function log(...args: any):void;
    function debug(...args: any):void;
    function info(message: string):void;
    function success(message: string):void;
    function failure(message: string):void;
    function spin(message: string, options: import("ora").Options):void;
    function stopSpinner():void;
    function isSpinning():boolean;
}
