/**
 * @enum {number}
 * @property {number} OFF
 * @property {number} ERROR
 * @property {number} WARN
 * @property {number} INFO
 * @property {number} DEBUG
 */
export const LOG_LEVEL = {
  OFF: 1,
  ERROR: 2,
  WARN: 4,
  INFO: 8,
  DEBUG: 16,
};

/**
 * @enum {number}
 */
export const LOG_RANGES = {
  ERROR: 0,
  WARN: 0,
  INFO: 0,
  DEBUG: 0,
};

LOG_RANGES.ERROR = LOG_LEVEL.ERROR | LOG_LEVEL.WARN | LOG_LEVEL.INFO | LOG_LEVEL.DEBUG;
LOG_RANGES.WARN = LOG_LEVEL.WARN | LOG_LEVEL.INFO | LOG_LEVEL.DEBUG;
LOG_RANGES.INFO = LOG_LEVEL.INFO | LOG_LEVEL.DEBUG;
LOG_RANGES.DEBUG = LOG_LEVEL.DEBUG;
