import { LOG_LEVEL } from './logLevels.js';

/**
 *
 * @type {{ useTimestamp: boolean, logLevel: LOG_LEVEL }}
 */
const Settings = {
  useTimestamp: false,
  logLevel: LOG_LEVEL.INFO,
};

/**
 * @param {string|LOG_LEVEL|boolean} logLevel
 */
export function setLogLevel(logLevel) {
  if (typeof logLevel === 'string') {
    const key = logLevel.toUpperCase();
    Settings.logLevel = LOG_LEVEL[key] || LOG_LEVEL.INFO;
  } else if (typeof logLevel === 'number') {
    Settings.logLevel = Object.values(LOG_LEVEL).includes(logLevel) ? logLevel : LOG_LEVEL.INFO;
  } else {
    Settings.logLevel = logLevel === true ? LOG_LEVEL.INFO : LOG_LEVEL.OFF;
  }
}

export default Settings;
